import { Component, OnInit } from '@angular/core';
import { TaskserviceService } from 'src/app/services/taskservice.service';
import { BsDatepickerConfig } from 'ngx-bootstrap/datepicker';

@Component({
  selector: 'app-taskboard',
  templateUrl: './taskboard.component.html',
  styleUrls: ['./taskboard.component.css'],
})
export class TaskboardComponent implements OnInit {
  data: any;
  newTask: any = {};
  bsConfig: Partial<BsDatepickerConfig>; // Configuration for date picker

  constructor(private dataService: TaskserviceService) {
    this.bsConfig = Object.assign({}, { containerClass: 'theme-default' });
  }

  ngOnInit() {
    this.getTask();
  }

  getTask() {
    this.dataService.getTaskData().subscribe((res) => {
      this.data = res;
      console.log(res);
    });
  }

  addTask() {
    const title = prompt('Enter the Title');
    const description = prompt('Enter the description');
    const priority = prompt('Enter the Priority');
    this.newTask = {
      title: title,
      description: description,
      priority: priority,
      DueDate: this.newTask.dueDate, // Use the selected due date
    };
    this.dataService.addTask(this.newTask).subscribe(
      () => {
        console.log('Task added successfully');
        this.getTask();
      },
      (error) => {
        console.error('Error adding task:', error);
      }
    );
  }
}
<button (click)="addTask()">Add Task</button>
<div class="task-cards">
  <div *ngFor="let task of data" class="task-card">
    <div class="card-header">
      <h3>{{ task.Title }}</h3>
    </div>
    <div class="card-content">
      <p><strong>Description:</strong> {{ task.Description }}</p>
      <p><strong>Due Date:</strong> {{ task.DueDate }}</p>
      <p><strong>Priority:</strong> {{ task.Priority }}</p>
    </div>
  </div>
</div>
