 // Import required modules and services
import { Component, OnInit } from '@angular/core';
import { TaskserviceService } from 'src/app/services/taskservice.service';

@Component({
  selector: 'app-taskboard',
  templateUrl: './taskboard.component.html',
  styleUrls: ['./taskboard.component.css']
})
export class TaskboardComponent implements OnInit {
  data: any;

  constructor(private dataService: TaskserviceService) {}

  ngOnInit() {
    this.getTask();
  }

  getTask() {
    this.dataService.getTaskData().subscribe((res) => {
      this.data = res;
    });
  }

  toggleEditMode(task: any) {
    task.editMode = !task.editMode;
    // Initialize updated fields when entering edit mode
    if (task.editMode) {
      task.updatedTitle = task.Title;
      task.updatedDescription = task.Description;
      task.updatedDueDate = task.DueDate;
      task.updatedPriority = task.Priority;
    }
  }

  saveTask(task: any) {
    // Call the service to update the task data
    this.dataService.updateTask(task._id, {
      Title: task.updatedTitle,
      Description: task.updatedDescription,
      DueDate: task.updatedDueDate,
      Priority: task.updatedPriority
    }).subscribe(
      () => {
        console.log('Task updated successfully');
        task.Title = task.updatedTitle;
        task.Description = task.updatedDescription;
        task.DueDate = task.updatedDueDate;
        task.Priority = task.updatedPriority;
        task.editMode = false;
      },
      (error) => {
        console.error('Error updating task:', error);
      }
    );
  }

  cancelEdit(task: any) {
    task.editMode = false;
    // Reset the priority back to its original value
    task.updatedPriority = task.Priority;
  }
}
 <div class="card-container">
  <div class="card" *ngFor="let task of data">
    <div class="card-header">
      {{ task.Title }}
      <div *ngIf="task.editMode">
        <input [(ngModel)]="task.updatedTitle" placeholder="Title">
      </div>
    </div>
    <div class="card-body">
      <p class="card-text">{{ task.Description }}</p>
      <div *ngIf="task.editMode">
        <input [(ngModel)]="task.updatedDescription" placeholder="Description">
      </div>
      <p class="card-text">Due Date: {{ task.DueDate }}</p>
      <div *ngIf="task.editMode">
        <input [(ngModel)]="task.updatedDueDate" type="date">
      </div>
      <p class="card-text">Priority: {{ task.Priority }}</p>
      <div *ngIf="task.editMode">
        <input [(ngModel)]="task.updatedPriority" placeholder="Priority">
      </div>

    </div>
    <div class="card-footer">
      <button *ngIf="!task.editMode" (click)="toggleEditMode(task)">Edit</button>
      <button *ngIf="task.editMode" (click)="saveTask(task)">Save</button>
      <button *ngIf="task.editMode" (click)="toggleEditMode(task)">Cancel</button>
    </div>
  </div>
</div>
