import { Component, OnInit } from '@angular/core';
import { TaskserviceService } from 'src/app/services/taskservice.service';

@Component({
  selector: 'app-taskboard',
  templateUrl: './taskboard.component.html',
  styleUrls: ['./taskboard.component.css']
})
export class TaskboardComponent implements OnInit {
  data: any;
  newTask: any = {}; // New task data object
  showAddForm: boolean = false; // Flag to toggle add task form
  searchKeyword: string = ''; // Add this line
  selectedPriority: string = 'all'; // Add this line


  constructor(private dataService: TaskserviceService) {}

  ngOnInit() {
    this.getTask();
  }

  getTask() {
    this.dataService.getTaskData().subscribe((res) => {
      this.data = res;
    });
  }

  toggleEditMode(task: any) {
    task.editMode = !task.editMode;
    if (task.editMode) {
      task.updatedTitle = task.Title;
      task.updatedDescription = task.Description;
      task.updatedDueDate = task.DueDate;
      task.updatedPriority = task.Priority;
    }
  }

  toggleAddForm() {
    this.showAddForm = !this.showAddForm;
  }

  addTask() {
    this.dataService.addTask(this.newTask).subscribe(
      () => {
        console.log('Task added successfully');
        this.getTask();
        this.newTask = {}; // Clear new task data
        this.showAddForm = false; // Hide add task form
      },
      (error) => {
        console.error('Error adding task:', error);
      }
    );
  }

  saveTask(task: any) {
    // Call the service to update the task data
    this.dataService.updateTask(task._id, {
      Title: task.updatedTitle,
      Description: task.updatedDescription,
      DueDate: task.updatedDueDate,
      Priority: task.updatedPriority
    }).subscribe(
      () => {
        console.log('Task updated successfully');
        task.Title = task.updatedTitle;
        task.Description = task.updatedDescription;
        task.DueDate = task.updatedDueDate;
        task.Priority = task.updatedPriority;
        task.editMode = false;
      },
      (error) => {
        console.error('Error updating task:', error);
      }
    );
  }

  deleteTask(taskId: string) {
    if (confirm('Are you sure you want to delete this task?')) {
      this.dataService.deleteTask(taskId).subscribe(
        () => {
          console.log('Task deleted successfully');
          this.getTask();
        },
        (error) => {
          console.error('Error deleting task:', error);
        }
      );
    }
  }
  filterTasks() {
    if (!this.data) {
      return [];
    }

    if (this.selectedPriority === 'all') {
      return this.data.filter((task: any) => task.Title.includes(this.searchKeyword));
    } else {
      return this.data.filter((task: any) =>
        task.Title.includes(this.searchKeyword) && task.Priority === this.selectedPriority
      );
    }
  }
}
