import { Component, OnInit } from '@angular/core';
import { TaskserviceService } from 'src/app/services/taskservice.service';

@Component({
  selector: 'app-taskboard',
  templateUrl: './taskboard.component.html',
  styleUrls: ['./taskboard.component.css']
})
export class TaskboardComponent implements OnInit {
  data: any;

  constructor(private dataService: TaskserviceService) {}

  ngOnInit() {
    this.getTask();
  }

  getTask() {
    this.dataService.getTaskData().subscribe((res) => {
      this.data = res;
    });
  }

  toggleEditMode(task: any): void {
    task.editMode = !task.editMode;
  }

  saveTask(task: any): void {
    const updatedTask = {
      Title: task.Title,
      Description: task.Description,
      DueDate: task.DueDate,
      Priority: task.Priority,
    };

    this.dataService.updateTask(task._id, updatedTask).subscribe(
      () => {
        console.log('Task updated successfully');
        task.editMode = false;
        this.getTask();
      },
      (error) => {
        console.error('Error updating task:', error);
      }
    );
  }
}
